import logo from '../../assets/logo.svg';
import './App.css';

import React, {useState} from 'react';
import Axios from 'axios';
import { Switch, Route } from "react-router-dom";
import CuisineList from '../CusineList';
import RestaurantDetail from '../RestaurantDetail';
import Header from '../Header';
import ReactLoading from 'react-loading';

function App() {
  const [cuisine, setCuisine] = React.useState({
    cuisine: [
      'hamburger',
      'pizza',
      'chicken',
      'vegetarian',
      'vegan',
      'fries',
      'seafood',
      'breakfast',
      'tacos',
      'asian'
    ]})

    const [location, setLocation] = React.useState();
    const [locationError, setLocationError] = React.useState();

    const handleTermChange = (e) => {
      setLocation({
        name: e.target.value
      });
    }

    React.useEffect(() => {

    }, [location])

    if (location) {
      return (
        <div className="App">
            <Switch>
                  <Route exact path ='/'>
                    <CuisineList cuisine={cuisine} />
                  </Route>
                  <Route exact path ='/:cuisine'>
                    <RestaurantDetail location={location}/>
                  </Route>
              </Switch>

        </div>
      )
    } else if (locationError) {
      return (
        <div className="App">
          <Header text="What's For Lunch?"/>
          <h1 style={{color: 'white', width: '100%', textAlign: 'center'}}>Hmm, couldn't get your location</h1>
          <input placeholder="Enter your location" onChange={handleTermChange()}></input>
          <ReactLoading type={'spin'} height={'10%'} width={'10%'} />
        </div>
      )
    } else {
      console.log('waiting')
      setTimeout(() => {
        navigator.geolocation.getCurrentPosition((position) => {
          if (position) {
            setLocation({
              latitude: position.coords.latitude,
              longitude: position.coords.longitude
            })
          }
        }, (err) => {
          setLocationError(err);
        })
      }, 500);
      return(
        <div className="App">
          <Header text="What's For Lunch?"/>
          <h1 style={{color: 'white', width: '100%', textAlign: 'center'}}>Getting your location</h1>
          <ReactLoading type={'spin'} height={'10%'} width={'10%'} />
        </div>
      )



    };
}

export default App;
