import React from 'react';
import './Header.css';

import Title from '../../elements/Title';
import { motion, AnimatePresence } from "framer-motion";


function Header() {
    const [show, setShow] = React.useState(true);
    const variants = {
        initial: { opacity: 0, x: '100%'},
        enter: { opacity: 1, x: 0 },
        exit: { opacity: 0, x: '100%', transition: {duration: 1.5}}
    }
    if (show) {
        return(
            <AnimatePresence>
                <motion.div
                    initial ="intiial"
                    animate="enter"
                    exit="exit"
                    variants={variants}>
                    <Title />
                </motion.div>            
            </AnimatePresence>

        )
    } else {
        return(
            <div>
                <form>
                    <input placeholder="Search for a city"></input>                    
                </form>
            </div>
        )
    }
}

export default Header;
