import './Yelp.css';

import yelp from '../../assets/business/yelp.png';
import { useEffect, useState } from "react";
import { motion, useViewportScroll, useTransform } from "framer-motion";

function Yelp({link}) {
    const [isHovered, setHovered] = useState(true);
    const { scrollYProgress } = useViewportScroll();
    const yRange = useTransform(scrollYProgress, [0, 0.9], [0, 1]);
    useEffect(() => yRange.onChange((v) => setHovered(v === 1)), [yRange]);
    return (
        <motion.div   whileHover={{
            backgroundColor: '#E0BE2F'
        }}
        style={{borderRadius: '50px', width: '80%'}}>
    <div style={{display: 'flex', alignItems: 'center', justifyContent: 'space-around'}} className="elements-yelp">
            <motion.div
                style={{width: '20%', height: 'auto'}}
                animate={{
                    opacity: isHovered ? 1 : 0,
                    x: isHovered ? 0 : -100 }}>
            <img src={yelp} style={{width: '100%', height: 'auto'}}/>
            </motion.div>
            <motion.div
                animate={{
                    opacity: isHovered ? 1: 0,
                    x: isHovered ? 0 : 100 }}>
                <a href={link} target="blank" style={{textDecoration: 'none', color: 'white', fontWeight: 'bold', marginRight: '20px'}}>open in Yelp</a>
            </motion.div>
        </div>
        </motion.div>


    )
}
export default Yelp;
