import React from 'react';
import './Header.css';
import Title from '../../elements/Title';

function Header({text, location}) {
    const [show, setShow] = React.useState(true);
    if (show) {
        return(           
            <div>
                <Title text={text}/> 
                <p>{location.name}</p>              
            </div>


        )
    } else {
        return(

            <div>
                <form>
                    <input placeholder="Search for a city"></input>                    
                </form>
            </div>
        )
    }
}

export default Header;
